    }

    //提问
    ask(que, msg){
        clearTimeout(window.timer);
        let that = this;
        window.layer.prompt({title: que}, function(text, index){
            layer.closeAll();
            if(text!=msg){
                window.layer.msg('正确答案：'+msg, {time: 1000});
                ///////////////////////////////////////////////////////
                $.ajax({
                    type:"POST",
                    url:"/collect",
                    data:{que:que,ans:text,msg:msg},
                    success: function(json){
                    },
                    error: function(){
                    }
                });
        ///////////////////////////////////////////////////////
                setTimeout(()=>{
                    that.animate();
                }, 1100);
            }else{
                that.animate();
            }
        } );
    }
    showCode(msg){
        let content = "";
        if(msg === 1){
            content = 
            `
        <p style="white-space:pre-wrap !important; padding: 30px;">    void DFS(Graph& G, int v)
        {
            <span style="color: red">visited[v] = 1;	</span>
