      var stepTemp = stepData.steps[actionStep.ACTION_COUNT].split('\t')[3]; //得到帧的编号

      if (stepData.steps[actionStep.ACTION_COUNT].split('\t')[1].split('(')[0] === "Question") {
        //question动作的帧编号位置不同!
        stepTemp = stepData.steps[actionStep.ACTION_COUNT].split('\t')[7];
      }

      var types = new Array(); //创建数组，用于保存动作信息

      var infos = new Array();

      while (actionStep.STEP_COUNT == stepTemp) {
        //读取一帧动作
        console.log("get action: " + actionStep.ACTION_COUNT);
        var info = stepData.steps[actionStep.ACTION_COUNT].split('\t'); //第ACTION_COUNT条动作信息(作为数组保存在info)                  

        var type = info[1].split('(')[0]; //info[1]代表动作的解释：changeNodeColor(4,RGB(91,155,213))；  则type对应changeNodeColor

        types.push(type);
        infos.push(info);
        actionStep.ACTION_COUNT++;
        if (stepData.steps.length - 1 <= actionStep.ACTION_COUNT) return null; //尚不清楚为什么stepData.steps.length比实际数组长度多了1??

        console.log("length:" + stepData.steps.length);
        console.log("action count:" + actionStep.ACTION_COUNT);
        stepTemp = stepData.steps[actionStep.ACTION_COUNT].split('\t')[3]; //得到帧的编号

        if (stepData.steps[actionStep.ACTION_COUNT].split('\t')[1].split('(')[0] === "Question") {
          //question动作的帧编号位置不同!
          stepTemp = stepData.steps[actionStep.ACTION_COUNT].split('\t')[7];
        }
      }

      return {
        types: types,
        infos: infos
      }; //返回对象，对象的属性是键值对
    }
