        case "(91,155,213))":
          return this.INITIAL_NODE_COLOR;
        //初始化点（接近蓝色)

        case "(255,0,0))":
          return this.SELECTED_LINK_COLOR;
        //选中点、边（红色）

        default:
          return "#000000";
      }
    }
